import React from "react";
import myImage from "../img/myImage.png";

class About extends React.Component {
  constructor() {
    super();
    this.state = {
      skills: [
        {
          id: "Python_skill",
          content: "Python",
          porcentage: "85%",
          value: "85"
        },
        {
          id: "Flask_skill",
          content: "Flask",
          porcentage: "85%",
          value: "85"
        },
        {
          id: "Django_skill",
          content: "Django",
          porcentage: "70%",
          value: "70"
        },
        {
          id: "ReactJS_skill",
          content: "ReactJS",
          porcentage: "75%",
          value: "75"
        },
        {
          id: "PHP_skill",
          content: "PHP", 
          porcentage: "65%", 
          value: "65" 
        },
        {
          id: "Laravel_skill",
          content: "Laravel",
          porcentage: "85%",
          value: "85"
        },
        {
          id: "Docker_skill",
          content: "Docker",
          porcentage: "70%",
          value: "70"
        },
        {
          id: "Ansible_skill",
          content: "Ansible",
          porcentage: "10%",
          value: "10"
        }
      ],
      about_me: {
        id: 1,
        content:
          `
          Hi, I am your friend and partner DREY. \n\n
          My goal is to help you solve your data cleaning task and to transform your unstructured data into a form easy to use and understand
          If you have a dataset and you would like it cleaned and converted to a nice format,then I am your
          guy.

          The challenges I focus on include \n\n
          \t- Data Cleaning in python
          \t- Data Analysis(EDA,Descriptive Analysis)
          \t- Convert dataset to csv,excel,tsv,etc
          \t- Simple web scraping

          What will you get? \n\n
          \n- Your challenge fixed
          \n- Cleaned Data-set
          \n- Periodical Support of the work
          \n- Fast delivery per complexity of data-set
          \n- And More

          What's the process? \n\n
          \n\n- Please send all the required details and sample dataset
          \n\n- Within a set time, I will send you the cleaned dataset to appraise
          \n\n- You evaluate the work and determine any changes that you want implemented
          \n\n- I will make the necessary revisions and resubmit them for final approval

          Please I would be glad if you message me before placing an order.
          Let's get in touch today
        `
      }
    };
  }

  render() {
    return (
      <section id="about" className="about-mf sect-pt4 route">
        <div className="container">
          <div className="row">
            <div className="col-sm-12">
              <div className="box-shadow-full">
                <div className="row">
                  <div className="col-md-5">
                    <div className="row">
                      <div
                        className="col-sm-6 col-md-5"
                        style={{ margin: "0 auto" }}
                      >
                        <div
                          className="about-img"
                          style={{ textAlign: "center" }}
                        >
                          <img
                            className="img-fluid rounded b-shadow-a"
                            alt=""
                          />
                        </div>
                      </div>
                    </div>
                    <div className="skill-mf">
                      {/* <p className="title-s">Skill</p> */}
                      {this.state.skills.map(skill => {
                        return (
                          <React.Fragment key={skill.id}>
                            <span>{skill.content}</span>{" "}
                            <span className="pull-right">
                              {skill.porcentage}
                            </span>
                            <div className="progress">
                              <div
                                className="progress-bar"
                                role="progressbar"
                                style={{ width: skill.porcentage }}
                                aria-valuenow={skill.value}
                                aria-valuemin="0"
                                aria-valuemax="100"
                              ></div>
                            </div>
                          </React.Fragment>
                        );
                      })}
                    </div>
                  </div>
                  <div className="col-md-7">
                    <div className="about-me pt-4 pt-md-0">
                      <div className="title-box-2">
                        <h5 className="title-left">About Me</h5>
                      </div>
                      {this.state.about_me.content.map(ctn => {
                        return (
                          <p className="lead" key={ctn.id}>                            
                            {ctn}
                          </p>
                        );
                      })}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default About;
